    public function getSourceContext()
    {
        return new Source("{% extends 'base-login.html.twig' %}

{% block title %}Connexion{% endblock %}

{% block stylesheets %}
<link href=\"{{ asset('css/sign-in.css') }}\" rel=\"stylesheet\">
{% endblock %}

{% block body %}
<main class=\"form-signin w-100 m-auto\">
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
        </div>
    {% endif %}

    <h1 class=\"h3 mb-3 fw-normal\">Veuillez vous connecter</h1>

    <div class=\"form-floating\">
        <input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" class=\"form-control\" placeholder=\"Pseudo\" autocomplete=\"username\" required autofocus>
        <label for=\"floatingInput\">Pseudo</label>
    </div>

    <div class=\"form-floating\">
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" placeholder=\"Mot de passe\" autocomplete=\"current-password\" required>
        <label for=\"floatingInput\">Mot de passe</label>
    </div>

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <button class=\"w-100 btn btn-lg btn-primary\" type=\"submit\">
        Connexion
    </button>
</form>
</main>
{% endblock %}
", "security/login.html.twig", "D:\\Projets\\data-processing\\templates\\security\\login.html.twig");
    }
